import CartContext from "./CartContext";
import React, {useReducer} from "react";

 const cartDefaultState = {
  items : [],
  totalAmount : 0

};

const cartReducer = (state,action) => {
  if(action.type === 'Add'){
    const updatedItems = state.items.concat(action.item);
    const updatedTotalamount = state.totalAmount + action.item.price * action.item.amount;
    return
  }
  return cartDefaultState;

};

const CartProvider = (props) => {
  const [cartState, cartDispatch] = useReducer(cartReducer, cartDefaultState);
  
  const addItemtoCart = (item) => {
    cartDispatch({type:"Add",item:item})
  };
  
  const delItemtoCart = (id) => {
    cartDispatch({type:"Remove",id:id})
  };

  const cartContex = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemtoCart,
    removeItem: delItemtoCart,
  };

  return <CartContext.Provider value={cartContex}>{props.children}</CartContext.Provider>;
};


export default CartProvider;
